import React, { useCallback, useEffect } from 'react';
import ReactFlow, {
  addEdge,
  MiniMap,
  Controls,
  Background,
  useNodesState,
  useEdgesState,
} from 'reactflow';
// import useWebSocket from 'react-use-websocket';
import { io } from 'socket.io-client';



import { nodes as initialNodes, edges as initialEdges } from './initial-elements';
import CustomNode from './CustomNode';

import 'reactflow/dist/style.css';
import './overview.css';

const nodeTypes = {
  custom: CustomNode,
};

const minimapStyle = {
  height: 120,
};

const onInit = (reactFlowInstance) => console.log('flow loaded:', reactFlowInstance);

const OverviewFlow = () => {
  const [nodes, setNodes, onNodesChange] = useNodesState(initialNodes);
  const [edges, setEdges, onEdgesChange] = useEdgesState(initialEdges);
  const onConnect = useCallback((params) => setEdges((eds) => addEdge(params, eds)), []);


  /* Flask SSE */
  // const initializeSSE = useCallback(() => {
  //   const sse = new EventSource('http://127.0.0.1:5000/sse');
  //   sse.onmessage = (event) => {
  //     const data = JSON.parse(event.data);

  //     if (data.type === 'addEdge') {
  //       setEdges((eds) => addEdge(data.edge, eds));
  //     }
  //   };
  //   sse.onerror = () => {
  //     console.error('SSE connection error');
  //   };
  //   return () => {
  //     sse.close();
  //   };
  // }, []);

  // useEffect(() => {
  //   const cleanup = initializeSSE();
  //   return cleanup;
  // }, [initializeSSE]);


  /* Sockets Approach 1 */
  // const socketUrl = 'ws://localhost:5000/sse';
  // console.log('Attempting to connect to WebSocket:', socketUrl); // Add this line to log WebSocket connection attempts
  // const { sendMessage, lastMessage, readyState } = useWebSocket(socketUrl);

  // useEffect(() => {
  //   console.log("WebSocket readyState:", readyState); // Add this line to log the WebSocket connection status
  // }, [readyState]);

  // useEffect(() => {
  //   if (lastMessage) {
  //     const data = JSON.parse(lastMessage.data);
  //     console.log('Received data:', data);
  //     if (data.type === 'addEdge') {
  //       setEdges((eds) => addEdge(data.edge, eds));
  //     }
  //   }
  // }, [lastMessage]);




  /* Sockets Approach 2*/
  useEffect(() => {
    const socket = io('http://localhost:5000');

    socket.on('addEdge', (data) => {
      if (data.type === 'addEdge') {
        setEdges((eds) => addEdge(data.edge, eds));
        setTimeout(()=> setEdges([]), 5000)
      }
    });

    return () => {
      socket.disconnect();
    };
  }, []);




  // we are using a bit of a shortcut here to adjust the edge type
  // this could also be done with a custom edge for example
  const edgesWithUpdatedTypes = edges.map((edge) => {
    if (edge.sourceHandle) {
      const edgeType = nodes.find((node) => node.type === 'custom').data.selects[edge.sourceHandle];
      edge.type = edgeType;
    }

    return edge;
  });

  return (
    <div class="row" style={{ width: '80vw', height: '100vh' }}>
        <ReactFlow
          nodes={nodes}
          edges={edgesWithUpdatedTypes}
          onNodesChange={onNodesChange}
          onEdgesChange={onEdgesChange}
          onConnect={onConnect}
          onInit={onInit}
          fitView
          attributionPosition="top-right"
          nodeTypes={nodeTypes}
        >
          <MiniMap style={minimapStyle} zoomable pannable />
          <Controls />
          <Background color="#aaa" gap={16} />
        </ReactFlow>
        <div style={{ width: '20vw', height: '100vh' }}>
          Hello
        </div>
    </div>
  );
};

export default OverviewFlow;
