import React, { useCallback, useEffect, useState } from 'react';
import ReactFlow, {
  addEdge,
  MiniMap,
  Controls,
  Background,
  useNodesState,
  useEdgesState,
} from 'reactflow';
// import useWebSocket from 'react-use-websocket';
import { io } from 'socket.io-client';



import { nodes as initialNodes, edges as initialEdges } from './initial-elements';
import CustomNode from './CustomNode';

import 'reactflow/dist/style.css';
import './overview.css';

const nodeTypes = {
  custom: CustomNode,
};

const minimapStyle = {
  height: 120,
};

const onInit = (reactFlowInstance) => console.log('flow loaded:', reactFlowInstance);

const OverviewFlow = () => {
  const [nodes, setNodes, onNodesChange] = useNodesState(initialNodes);
  const [edges, setEdges, onEdgesChange] = useEdgesState(initialEdges);
  const [initialAsk, setInitialAsk] = useState("")
  const [messages, setMessages] = useState([]);
  const onConnect = useCallback((params) => setEdges((eds) => addEdge(params, eds)), []);

  /* Sockets Approach */
  useEffect(() => {
    const socket = io('http://localhost:5000');

    socket.on('addEdge', (data) => {
      if (data.type === 'addEdge') {
        setMessages((prevMessages) => [...prevMessages, data.edge.label]);
        setEdges([])
        setEdges((eds) => addEdge(data.edge, eds));
      }
    });

    socket.on('initialAsk', (data) => {
      if (data.type === 'initialAsk') {
        console.log('Initial request occurred');
      }
    });

    socket.on('askFinished', (data) => {
      if (data.type === 'askFinished') {
        console.log('Ask finished');
      }
    });

    return () => {
      socket.disconnect();
    };
  }, []);


  // we are using a bit of a shortcut here to adjust the edge type
  // this could also be done with a custom edge for example
  const edgesWithUpdatedTypes = edges.map((edge) => {
    if (edge.sourceHandle) {
      const edgeType = nodes.find((node) => node.type === 'custom').data.selects[edge.sourceHandle];
      edge.type = edgeType;
    }

    return edge;
  });

  return (
    <div class="row" style={{ width: '100vw', height: '100vh' }}>
        <ReactFlow
          nodes={nodes}
          edges={edgesWithUpdatedTypes}
          onNodesChange={onNodesChange}
          onEdgesChange={onEdgesChange}
          onConnect={onConnect}
          onInit={onInit}
          fitView
          attributionPosition="top-right"
          nodeTypes={nodeTypes}
          style={{ width: '60%', height: '100%' }}
        >
          <MiniMap style={minimapStyle} zoomable pannable />
          <Controls />
          <Background color="#aaa" gap={16} />
        </ReactFlow>
        <div style={{ width: '40%', height: '100%' }}>
          <ul>
            {messages.map((item, index) => (
              <li key={index}>{item}</li>
            ))}
          </ul>
        </div>
    </div>
  );
};

export default OverviewFlow;
